import React from 'react';
import { takeEvery, call, put, select, flush, delay } from "redux-saga/effects";
import { coinApi } from "../Api/api";

const START_INIT = "START_INIT";
const GET_MARKET_NAMES = "GET_MARKET_NAMES";

const getMarketNameSaga = () => ({ type: GET_MARKET_NAMES });
const startInit = () => ({ type: START_INIT });
/*
const createRequestSaga = (type, api) => {
    const SUCCESS = `${type}_SUCCESS`;
    const ERROR = `${type}_ERROR`;

    return function* (action = {}) {
        // yield put(startLoading(type));
        const res = yield call(api, action.payload);
        console.log(res);
        // yield put(finishLoading(type));
    }
};
*/
function* startInitSaga() {
    yield delay(2000);
    yield put(getMarketNameSaga());
}
function* coinSaga() {

    yield takeEvery(START_INIT, startInitSaga);
}

/*
const initialState = {
    symbol: "",
    upbitSymbol: "",
    bithumbSymbol: "",
    huobiSymbol: "",
    binanceSymbol: "",

    upbitPrice: "",
    bithumbPrice: "",
    huobiPrice: "",
    binancePrice: "",
};*/

const coinReducer = (state = 0, action) => {
    switch (action.type) {
        case GET_MARKET_NAMES:
            console.log(11);
            return state + 1;
        case START_INIT:
            console.log(12);
            return state + 1;
        default:
            return state;

    }
}
export {
    coinReducer,
    coinSaga,
    startInit,
    getMarketNameSaga
}
