import React from 'react';
import { takeEvery, call, put, select, flush, delay } from "redux-saga/effects";
import { coinApi } from "../Api/api";
import { createConnectSocketSaga, createRequestSaga, requestActions } from '../Lib/asyncUtil';
import { coinListDataUtils } from '../Lib/utils';

const START_INIT = "START_INIT";
const START_INIT_ASYNC = "START_INIT_ASYNC";
const GET_MARKET_NAMES = "GET_MARKET_NAMES";
const GET_MARKET_NAMES_SUCCESS = "GET_MARKET_NAMES_SUCCESS";
const GET_MARKET_NAMES_ERROR = "GET_MARKET_NAMES_ERROR";

const CONNECT_UPBIT_SOCKET = "CONNECT_UPBIT_SOCKET";
const CONNECT_UPBIT_SOCKET_SUCCESS = "CONNECT_UPBIT_SOCKET_SUCCESS";
const CONNECT_UPBIT_SOCKET_ERROR = "CONNECT_UPBIT_SOCKET_ERROR";

const startInitAsync = () => ({ type: START_INIT_ASYNC });
const startInit = () => ({ type: START_INIT });


const getMarketNameSaga = createRequestSaga(
    GET_MARKET_NAMES,
    coinApi.getMarketCodes,
    coinListDataUtils.marketNames
);


const connectUpbitSocketSaga = createConnectSocketSaga(
    CONNECT_UPBIT_SOCKET,
    "marketNames",
    coinListDataUtils.upbit
);


function* startInitSaga() {
    yield getMarketNameSaga();

    const state = yield select();
    const marketNames = Object.keys(state.Coin.marketNames.data);

    yield connectUpbitSocketSaga({ payload: marketNames });

    //console.log(state.Coin.marketNames.data['ADA'].upbitSym);
}
function* coinSaga() {
    yield takeEvery(START_INIT, startInitSaga);
}

const initialState = {
    marketNames: {
        error: false,
        data: {
            "BTC": {
                upbitUSDT: "", upbitBTC: "", upbitSym: "", bithumbSym: "", binanSym: "", upbitPrice: 0.0, upbitBTCPrice: 0.0, upbitPrice: 0.0, bithumbPrice: 0.0, binanPrice: 0.0
            },
        },
    },
};

const coinReducer = (state = initialState, action) => {
    //console.log(state);
    switch (action.type) {
        case GET_MARKET_NAMES_SUCCESS:
        case GET_MARKET_NAMES_ERROR:
            return requestActions(GET_MARKET_NAMES, "marketNames")(state, action);

        case CONNECT_UPBIT_SOCKET_SUCCESS:
        case CONNECT_UPBIT_SOCKET_ERROR:
            return requestActions(CONNECT_UPBIT_SOCKET, "marketNames")(state, action);

        case START_INIT:
            //console.log(12);
            return state + 1;
        default:
            return state;
    }
}
export {
    coinReducer,
    coinSaga,
    startInit,
    getMarketNameSaga,
    connectUpbitSocketSaga,
    startInitAsync
}
