import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from 'react-redux';
import CoinContainer from "./container/CoinContainer";
import { startInit, getMarketNameSaga, startInitAsync } from './Reducer/coinReducer';
import "./App.css";
import { authService } from "fbase";
import AppRouter from "./Router";
import { collection, addDoc, getFirestore, getDocs } from "firebase/firestore"

function App() {

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(startInit());

  }, [dispatch]);

  const [init, setInit] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  useEffect(async () => {
    authService.onAuthStateChanged((user) => {
      if (user) {

        const authCheck = async () => {
          var logdBoolean = false;
          const ipData = await fetch('https://geolocation-db.com/json/');
          const locationIp = await ipData.json();
          const ipv4 = locationIp.IPv4;

          console.log("ipv4", ipv4);


          const db = await getFirestore();
          const querySnapshot = await getDocs(collection(db, "users"));
          let check = ""; let checkID = "";
          querySnapshot.forEach((doc) => {
            if (doc.data().useremail === user.email) {
              check = "emailOK";
              if (doc.data().ipaddress === ipv4) {
                check = "ipOK";
              } else if (doc.data().ipaddress === 'init') {
                check = "NEW";
                checkID = doc.id;
                doc.id.delete;
              } else {
                check = "FAIL";
              }
            }
          });

          //dbService.doc(users/id).delete();
          if (check === 'ipOK')
            setIsLoggedIn(true);
          else if (check === 'NEW') {
            await addDoc(collection(db, "users"), {
              useremail: user.email,
              ipaddress: ipv4,
              id: checkID,
            });
          } else if (check === 'FAIL')
            setIsLoggedIn(false);

        }




        //setIsLoggedIn(true);

        authCheck();



      } else {
        setIsLoggedIn(false);
      }
      setInit(true);
      //console.log(user);
    });
  }, [])

  /*
<div className="coin-app">
      <CoinContainer />
    </div>
  */
  return (
    <>
      {init ? <AppRouter isLoggedIn={isLoggedIn} /> : 'Initializing....'}
      <footer>&copy; Ncrypto {new Date().getFullYear()} </footer>
    </>
  );
}

export default App;
