import { takeEvery, call, put, select, flush, delay } from "redux-saga/effects";

import jsonGeckoData from "../Api/geckoallcoin.json";

const coinListDataUtils = {
    marketNames: (names) => {
        const data = {};
        names.forEach(name => {
            let shortSym = name.market.split("-")[1];
            let moneySym = name.market.split("-")[0];

            if (moneySym === "KRW") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitSym: name.market
                }
            } else if (moneySym === "USDT") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitUSDT: name.market
                }
            } else if (moneySym === "BTC") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitBTC: name.market
                }
            }
        });
        return data;
    },
    upbitAllNames: (names) => {
        const data = {};
        names.forEach(name => {
            data[name.market] = {
                korean: name.korean_name,
                english: name.english_name,
            };
        })
        return data;
    }
};

const coinReadDataUtils = {
    updates: (names, state) => {
        const coinStateDatas = state.Coin.marketNames.data;
        //console.log(coinStateDatas);
        names.forEach(name => {
            //const newData = { ...coinStateDatas };
            let shortSym = name.code.split("-")[1];
            let moneySym = name.code.split("-")[0];
            let nowPrice = name.trade_price;

            if (nowPrice < 1.0) {
                nowPrice = name.trade_price.toFixed(8);
            }

            //console.log("1", shortSym, "2", coinStateDatas[shortSym]);
            //if (shortSym === coinStateDatas[shortSym]) {
            if (moneySym === "KRW") {
                coinStateDatas[shortSym] = {
                    ...coinStateDatas[shortSym],
                    upbitPrice: nowPrice
                }
                //return coinStateDatas;
            } else if (moneySym === "USDT") {
                coinStateDatas[shortSym] = {
                    ...coinStateDatas[shortSym],
                    upbitUSDTPrice: nowPrice
                }
                //return coinStateDatas;
            } else if (moneySym === "BTC") {
                coinStateDatas[shortSym] = {
                    ...coinStateDatas[shortSym],
                    upbitBTCPrice: nowPrice
                }
                //return coinStateDatas;
            }
            //console.log("success", name.code, name.trade_price, coinStateDatas);
            //}
        });
        return coinStateDatas;
    },
    initNames: (names) => {
        const data = {};

        names.forEach(name => {

            let shortSym = name.split("-")[1];
            let moneySym = name.split("-")[0];

            let totalNameSym = jsonGeckoData.map((gecko) => {
                if (gecko.symbol === shortSym) {
                    console.log(gecko.symbol);

                    return gecko.name;
                }
            })

            if (moneySym === "KRW") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitPrice: 0.0,
                    upbitSym: name,
                    totalNameSym: totalNameSym,
                }
            } else if (moneySym === "USDT") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitUSDTPrice: 0.0,
                    upbitUSDT: name,
                    totalNameSym: totalNameSym,
                }
            } else if (moneySym === "BTC") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitBTCPrice: 0.0,
                    upbitBTC: name,
                    totalNameSym: totalNameSym,
                }
            }
        });
        return data;
    }
};


export {
    coinListDataUtils,
    coinReadDataUtils
}
