import { takeEvery, call, put, select, flush, delay } from "redux-saga/effects";
import axios from "axios";
import CoinMarketData from "../Api/CoinMarketData.json";

const coinListDataUtils = {
    marketNames: (names) => {
        const data = {};
        names.forEach(name => {
            let shortSym = name.market.split("-")[1];
            let moneySym = name.market.split("-")[0];
            if (moneySym === "KRW") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitSym: name.market
                }
            } else if (moneySym === "USDT") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitUSDT: name.market
                }
            } else if (moneySym === "BTC") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitBTC: name.market
                }
            }
        });
        return data;
    },
    upbitAllNames: (names) => {
        const data = {};
        names.forEach(name => {
            data[name.market] = {
                korean: name.korean_name,
                english: name.english_name,
            };
        })
        return data;
    }
};

const coinReadDataUtils = {
    updates: (names, state) => {
        const coinStateDatas = state.Coin.marketNames.data;
        //console.log(coinStateDatas);
        names.forEach(name => {
            //const newData = { ...coinStateDatas };
            let shortSym = name.code.split("-")[1];
            let moneySym = name.code.split("-")[0];
            let nowPrice = name.trade_price;

            if (nowPrice < 1.0) {
                nowPrice = name.trade_price.toFixed(8);
            }

            //console.log("1", shortSym, "2", coinStateDatas[shortSym]);
            //if (shortSym === coinStateDatas[shortSym]) {
            if (moneySym === "KRW") {
                coinStateDatas[shortSym] = {
                    ...coinStateDatas[shortSym],
                    upbitPrice: nowPrice
                }
                //return coinStateDatas;
            } else if (moneySym === "USDT") {
                coinStateDatas[shortSym] = {
                    ...coinStateDatas[shortSym],
                    upbitUSDTPrice: nowPrice
                }
                //return coinStateDatas;
            } else if (moneySym === "BTC") {
                coinStateDatas[shortSym] = {
                    ...coinStateDatas[shortSym],
                    upbitBTCPrice: nowPrice
                }
                //return coinStateDatas;
            }
            //console.log("success", name.code, name.trade_price, coinStateDatas);
            //}
        });
        return coinStateDatas;
    },
    initNames: (names) => {
        const data = {};

        names.forEach(name => {

            let shortSym = name.split("-")[1];
            let moneySym = name.split("-")[0];
            /*
                        let geckoSymbol = jsonGeckoData.find((gecko) => {
                            if (gecko.symbol === shortSym.toLowerCase()) {
                                return gecko;
                            }
                        });
            /*
                        axios.get(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=${Object.values(geckoSymbol)[0]}&per_page=100&page=1&sparkline=false`).then(
                            res => {
                                console.log(res.data);
                            }
                        ).catch(error => console.log(error));
            */

            //if (geckoSymbol) {
            let imgsrc = "";
            CoinMarketData.find((coin) => {
                if (coin.symbol === shortSym) {
                    imgsrc = coin.imgsrc;
                }
            })

            if (moneySym === "KRW") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitPrice: 0.0,
                    upbitSym: name,
                    imgsrc: imgsrc

                }
            } else if (moneySym === "USDT") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitUSDTPrice: 0.0,
                    upbitUSDT: name,
                    imgsrc: imgsrc

                }
            } else if (moneySym === "BTC") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitBTCPrice: 0.0,
                    upbitBTC: name,
                    imgsrc: imgsrc

                }
            }
            //}

        });
        return data;
    }
};


export {
    coinListDataUtils,
    coinReadDataUtils
}
