const coinListDataUtils = {
    marketNames: (names) => {
        const data = {};

        //console.log(names);
        names.forEach(name => {
            /*if (name.market.split("-")[0] !== "KRW")
                data[name.market] = {
                    korean: name.korean_name
                };*/
            let shortSym = name.market.split("-")[1];
            let moneySym = name.market.split("-")[0];
            //for (let checkSym in data) {
            if (shortSym === data[name.market]) {
                if (moneySym === "KRW") {
                    data[shortSym] = {
                        upbitSym: name.market
                    }
                } else if (moneySym === "USDT") {
                    data[shortSym] = {
                        upbitUSDT: name.market
                    }
                } else if (moneySym === "BTC") {
                    data[shortSym] = {
                        upbitBTC: name.market
                    }
                }
                //break;
            } else {
                if (moneySym === "KRW") {
                    data[shortSym] = {
                        upbitSym: name.market
                    }
                } else if (moneySym === "USDT") {
                    data[shortSym] = {
                        upbitUSDT: name.market
                    }
                } else if (moneySym === "BTC") {
                    data[shortSym] = {
                        upbitBTC: name.market
                    }
                }
            }
            //}
        });
        console.log(data);
        return data;
    },
};

export {
    coinListDataUtils
}
