import { takeEvery, call, put, select, flush, delay } from "redux-saga/effects";
import axios from "axios";
import CoinMarketData from "../Api/CoinMarketData.json";

const coinListDataUtils = {
    marketNames: (names) => {
        const data = {};
        names.forEach(name => {
            let shortSym = name.market.split("-")[1];
            let moneySym = name.market.split("-")[0];
            if (moneySym === "KRW") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitSym: name.market
                }
            } else if (moneySym === "USDT") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitUSDT: name.market
                }
            } else if (moneySym === "BTC") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitBTC: name.market
                }
            }
        });
        return data;
    },
    upbitAllNames: (names) => {
        const data = {};
        names.forEach(name => {
            data[name.market] = {
                korean: name.korean_name,
                english: name.english_name,
            };
        })
        return data;
    },
    bithumbKRWNames: (names) => {
        const data = {};
        //console.log(Object.keys(names));
        Object.keys(names).forEach(name => {
            data[name + "_KRW"] = {
                korean: "",
                english: "",
            };
        })
        console.log("11");
        return data;
    },
    bithumbBTCNames: (names, state) => {
        const coinStateDatas = state.Coin.bithumbTotalNames.data;

        Object.keys(names).forEach(name => {
            coinStateDatas[name + "_BTC"] = {
                korean: "",
                english: "",
            };
        })
        return coinStateDatas;
    }
};

const coinReadDataUtils = {
    updates: (names, state) => {
        const coinStateDatas = state.Coin.marketNames.data;
        //console.log(coinStateDatas);
        names.forEach(name => {
            //const newData = { ...coinStateDatas };
            let shortSym = name.code.split("-")[1];
            let moneySym = name.code.split("-")[0];
            let nowPrice = name.trade_price;

            if (nowPrice < 1.0) {
                nowPrice = name.trade_price.toFixed(8);
            }

            //console.log("1", shortSym, "2", coinStateDatas[shortSym]);
            //if (shortSym === coinStateDatas[shortSym]) {
            if (moneySym === "KRW") {
                coinStateDatas[shortSym] = {
                    ...coinStateDatas[shortSym],
                    upbitPrice: nowPrice
                }
                //return coinStateDatas;
            } else if (moneySym === "USDT") {
                coinStateDatas[shortSym] = {
                    ...coinStateDatas[shortSym],
                    upbitUSDTPrice: nowPrice
                }
                //return coinStateDatas;
            } else if (moneySym === "BTC") {
                coinStateDatas[shortSym] = {
                    ...coinStateDatas[shortSym],
                    upbitBTCPrice: nowPrice
                }
                //return coinStateDatas;
            }
            //console.log("success", name.code, name.trade_price, coinStateDatas);
            //}
        });
        return coinStateDatas;
    },
    upbitInitNames: (names) => {
        const data = {};
        names.forEach(name => {
            let shortSym = name.split("-")[1];
            let moneySym = name.split("-")[0];
            let imgsrc = "";
            CoinMarketData.find((coin) => {
                if (coin.symbol === shortSym) {
                    imgsrc = coin.imgsrc;
                }
            })
            if (moneySym === "KRW") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitPrice: 0.0,
                    upbitSym: name,
                    imgsrc: imgsrc
                }
            } else if (moneySym === "USDT") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitUSDTPrice: 0.0,
                    upbitUSDT: name,
                    imgsrc: imgsrc
                }
            } else if (moneySym === "BTC") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitBTCPrice: 0.0,
                    upbitBTC: name,
                    imgsrc: imgsrc
                }
            }
        });
        console.log(data);
        return data;
    },
    bithumbInitNames: (names) => {


    }
};


export {
    coinListDataUtils,
    coinReadDataUtils
}
