import { call, put, select, flush, delay } from "redux-saga/effects";
import { w3cwebsocket as W3CWebSocket } from "websocket";
import { buffers, eventChannel, END } from "redux-saga";
import encoding from "text-encoding";
import { bithumbcoinApi } from "../Api/api";

const createRequestSaga = (type, api, dataMaker) => {
    const SUCCESS = `${type}_SUCCESS`;
    const ERROR = `${type}_ERROR`;

    return function* (action = {}) {
        /*var res;
        var ress;
        if (type === "GET_BITHUMB_MARKET_NAMES") {
            res = yield call(bithumbcoinApi.getKRWMarketCodes);
