import { takeEvery, delay, put, select, take } from "redux-saga/effects";
import React from 'react';

const INCREASE = "INCREASE";
const INCREASE_ASYNC = "INCREASE_ASYNC";
const CONNECT_UPBIT_SOCKET = "UPBIT_SOCKET";

export const connectUpbitSocket = () => ({
    type: "CONNECT_UPBIT_SOCKET"
});

export const increase = () => ({ type: INCREASE });

function* increaseSaga() {
    yield delay(1000);
    yield put(increase());
}


function* connectcoinSaga() {
    yield delay(1000);
}


const initalState = 0;
export default function processCoin(state = initalState, action) {
    switch (action.type) {
        case INCREASE:
            return state + 1;
        default:
            return state;
    }
}

export function* processCoinSaga() {
    //yield takeEvery(CONNECT_UPBIT_SOCKET, connectcoinSaga);
    yield takeEvery(INCREASE_ASYNC, increaseSaga);
}
