import { takeEvery, delay, put, select, take } from "redux-saga/effects";
import React from 'react';
import { coinApi } from "../Api/api";
import { startLoading, finishLoading } from "../Reducer/loadingReducer";

const START_INIT = "START_INIT";
const GET_MARKET_NAMES = "GET_MARKET_NAMES";

const getMarketNameSaga = createRequestSaga(
    GET_MARKET_NAMES,
    coinApi.getMarketcodes
);

const createRequestSaga = (type, api, dataMaker) => {
    return function* (action = {}) {
        yield put(startLoading(type));
        const res = yield call(api, action.payload);
        console.log(res);
        yield put(finishLoading(type));

    }
};



const startInit = () => ({ type: START_INIT });
function* startInitSaga() {
    yield getMarketNameSaga();
}






function* coinSaga() {
    yield takeEvery(START_INIT, startInitSaga);
    yield takeEvery(GET_MARKET_NAMES, getMarketNameSaga);
}
const initialState = {
    symbol: "",
    upbitSymbol: "",
    bithumbSymbol: "",
    huobiSymbol: "",
    binanceSymbol: "",

    upbitPrice: "",
    bithumbPrice: "",
    huobiPrice: "",
    binancePrice: "",
};

const coinReducer = (state = initialSate, action) => {
    switch (action.type) {
        case GET_MARKET_NAMES:

            return
        default:
            return state;

    }
}

export {
    startInit,
    coinSaga,
    coinReducer
}
