import { takeEvery, call, put, select, flush, delay } from "redux-saga/effects";

const coinListDataUtils = {
    marketNames: (names) => {
        const data = {};
        names.forEach(name => {
            let shortSym = name.market.split("-")[1];
            let moneySym = name.market.split("-")[0];

            if (moneySym === "KRW") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitSym: name.market
                }
            } else if (moneySym === "USDT") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitUSDT: name.market
                }
            } else if (moneySym === "BTC") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitBTC: name.market
                }
            }
        });
        return data;
    },
    upbitAllNames: (names) => {
        const data = {};
        names.forEach(name => {
            data[name.market] = {
                korean: name.korean_name,
                english: name.english_name,
            };
        })
        return data;
    },
    stateInit: (names) => {
        const data = {};
        names.forEach(name => {
            data[name.market] = {
                korean: name.korean_name,
                english: name.english_name,
            };
        })
        return data;
    }
};

const coinReadDataUtils = {
    marketNames: (names) => {
        const data = {};
        names.forEach(name => {

            let shortSym = name.code.split("-")[1];
            let moneySym = name.code.split("-")[0];

            if (moneySym === "KRW") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitPrice: name.trade_price
                }
            } else if (moneySym === "USDT") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitUSDTPrice: name.trade_price
                }
            } else if (moneySym === "BTC") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitBTCPrice: name.trade_price
                }
            }
        });
        return data;
    },
    updates: (names, state) => {
        const candleStateDatas = state.Coin.marketNames.data;
        console.log(candleStateDatas);
        //const newData = { ...candleStateDatas };
    }
};


export {
    coinListDataUtils,
    coinReadDataUtils
}
