import React from 'react';
import { takeEvery, call, put, select, flush, delay } from "redux-saga/effects";
import { coinApi } from "../Api/api";
import { createRequestSaga, requestActions } from '../Lib/asyncUtil';
import { coinListDataUtils } from '../Lib/utils';


const START_INIT = "START_INIT";
const START_INIT_ASYNC = "START_INIT_ASYNC";
const GET_MARKET_NAMES = "GET_MARKET_NAMES";


const startInitAsync = () => ({ type: START_INIT_ASYNC });
const startInit = () => ({ type: START_INIT });


const getMarketNameSaga = createRequestSaga(
    GET_MARKET_NAMES,
    coinApi.getMarketCodes,
    coinListDataUtils.marketNames
);





/*
const createRequestSaga = (type, api) => {
    const SUCCESS = `${type}_SUCCESS`;
    const ERROR = `${type}_ERROR`;

    return function* (action = {}) {
        // yield put(startLoading(type));
        const res = yield call(api, action.payload);
        console.log(res);
        // yield put(finishLoading(type));
    }
};
*/
function* startInitSaga() {
    yield delay(2000);
    yield put(getMarketNameSaga());
    //yield takeEvery(GET_MARKET_NAMES, getMarketNameSaga);
}
function* coinSaga() {
    //yield takeEvery(START_INIT, startInitSaga);
    yield takeEvery(GET_MARKET_NAMES, getMarketNameSaga);
}


const initialState = {
    selectedMarket: "KRW-BTC",
    selectedTimeType: "minutes",
    selectedTimeCount: 5,
    selectedAskBidOrder: "bid",
    orderPrice: 0,
    orderAmount: 0,
    orderTotalPrice: 0,
    searchCoin: "",
    marketNames: {
        error: false,
        data: {
            "KRW-BTC": "비트코인",
        },
    },
    candle: {
        error: false,
        data: {
            "KRW-BTC": {
                candles: [
                    // { date: new Date(), open: 1, close: 1, high: 1, low: 1, volume: 1 },
                ],
                tradePrice24Hour: 0,
                volume24Hour: 0,
                changeRate24Hour: 0,
            },
        },
    },
    orderbook: {
        error: false,
        data: {
            "KRW-BTC": {
                total_bid_size: 0,
                total_ask_size: 0,
                orderbook_units: [],
            },
        },
    },
    tradeList: {
        error: false,
        data: {},
    },
};

const coinReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_MARKET_NAMES:
            return requestActions(GET_MARKET_NAMES, "marketNames")(state, action);
        case START_INIT:
            console.log(12);
            return state + 1;
        default:
            return state;

    }
}
export {
    coinReducer,
    coinSaga,
    startInit,
    getMarketNameSaga,
    startInitAsync
}
