import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import CoinMarketData from "../Api/CoinMarketData.json";

import "../components/CoinList.css";
function CoinList({ coins }) {
    const upbitCoinList = Object.keys(coins);
    const upbitimgsrc = CoinMarketData[0].imgsrc;


    /*
        upbitCoinList.map((coinSym) => {
            if (coinSym === 'BTC') {
                console.log(coins[coinSym]);
            }
        })*/

    return (
        <ul className="coin-container">
            <div >
                {
                    upbitCoinList.map(coinSym => (
                        <li className="coin-row" key={`coinlist-${coinSym}`}>
                            <div className="coin">
                                <img src={coins[coinSym].imgsrc} />
                                <h1>{coinSym}</h1>
                            </div>

                            {coins[coinSym].upbitSym ? <img className="exchange-img" src={upbitimgsrc} /> :
                                <div></div>
                            }
                            <div className="coin-data">
                                {coins[coinSym].upbitSym ?
                                    <p className="coin-price">
                                        {coins[coinSym].upbitSym}
                                    </p> :
                                    <span>
                                    </span>
                                }
                                {coins[coinSym].upbitSym ?
                                    <p className="coin-price">
                                        {coins[coinSym].upbitPrice}
                                    </p> :
                                    <span>
                                    </span>
                                }
                            </div>

                            {coins[coinSym].upbitBTC ? <img className="exchange-img" src={upbitimgsrc} /> :
                                <div></div>
                            }
                            <div classame="coin-data">
                                {coins[coinSym].upbitBTC ?
                                    <p className="coin-price">
                                        {coins[coinSym].upbitBTC}
                                    </p> :
                                    <span>
                                    </span>
                                }
                                {coins[coinSym].upbitBTC ?
                                    <p className="coin-price">
                                        {coins[coinSym].upbitBTCPrice}
                                    </p> :
                                    <span>

                                    </span>
                                }
                            </div>

                            {coins[coinSym].upbitUSDT ? <img className="exchange-img" src={upbitimgsrc} /> :
                                <div></div>
                            }
                            <div classame="coin-data">
                                {coins[coinSym].upbitUSDT ?
                                    <p className="coin-price">
                                        {coins[coinSym].upbitUSDT}
                                    </p> :
                                    <span>
                                    </span>
                                }
                                {coins[coinSym].upbitUSDT ?
                                    <p className="coin-price">
                                        {coins[coinSym].upbitUSDTPrice}
                                    </p> :
                                    <span>

                                    </span>
                                }
                            </div>

                            <div classame="coin-data">
                                {coins[coinSym].bithumbSym ?
                                    <p className="coin-price">
                                        {coins[coinSym].bithumbSym}
                                    </p> :
                                    <span>
                                    </span>
                                }
                                {coins[coinSym].bithumbSym ?
                                    <p className="coin-price">
                                        {coins[coinSym].bithumbPrice}
                                    </p> :
                                    <span>

                                    </span>
                                }
                            </div>



                        </li>
                    ))
                }
            </div>
        </ul>
    )
}

export default CoinList;

