import React from 'react';
import { takeEvery, call, put, select, flush, delay } from "redux-saga/effects";
import { coinApi } from "../Api/api";
import { createRequestSaga, requestActions } from '../Lib/asyncUtil';
import { coinListDataUtils } from '../Lib/utils';





const START_INIT = "START_INIT";
const START_INIT_ASYNC = "START_INIT_ASYNC";
const GET_MARKET_NAMES = "GET_MARKET_NAMES";
const GET_MARKET_NAMES_SUCCESS = "GET_MARKET_NAMES_SUCCESS";
const GET_MARKET_NAMES_ERROR = "GET_MARKET_NAMES_ERROR";



const startInitAsync = () => ({ type: START_INIT_ASYNC });
const startInit = () => ({ type: START_INIT });


const getMarketNameSaga = createRequestSaga(
    GET_MARKET_NAMES,
    coinApi.getMarketCodes,
    coinListDataUtils.marketNames
);


function* startInitSaga() {
    yield getMarketNameSaga();

    const state = yield select();
    //const marketNames = Object.keys(state.Coin.marketNames.data);
    //console.log(marketNames);
}
function* coinSaga() {
    yield takeEvery(START_INIT, startInitSaga);

}


const initialState = {
    selectedMarket: "KRW-BTC",
    selectedTimeType: "minutes",
    selectedTimeCount: 5,
    selectedAskBidOrder: "bid",
    orderPrice: 0,
    orderAmount: 0,
    orderTotalPrice: 0,
    searchCoin: "",
    marketNames: {
        error: false,
        data: {
            "KRW-BTC": "비트코인",
        },
    },
};

const coinReducer = (state = initialState, action) => {
    //console.log(state);
    switch (action.type) {
        case GET_MARKET_NAMES_SUCCESS:
        case GET_MARKET_NAMES_ERROR:
            //console.log("13");
            //console.log(coinListDataUtils.marketNames);
            return requestActions(GET_MARKET_NAMES, "marketNames")(state, action);
        //return state;
        case START_INIT:
            //console.log(12);
            return state + 1;
        default:
            return state;
    }
}
export {
    coinReducer,
    coinSaga,
    startInit,
    getMarketNameSaga,
    startInitAsync
}
