import { takeEvery, call, put, select, flush, delay } from "redux-saga/effects";

const coinListDataUtils = {
    marketNames: (names) => {
        const data = {};
        names.forEach(name => {
            let shortSym = name.market.split("-")[1];
            let moneySym = name.market.split("-")[0];

            if (moneySym === "KRW") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitSym: name.market
                }
            } else if (moneySym === "USDT") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitUSDT: name.market
                }
            } else if (moneySym === "BTC") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitBTC: name.market
                }
            }
        });
        return data;
    },
    upbitAllNames: (names) => {
        const data = {};
        names.forEach(name => {
            data[name.market] = {
                korean: name.korean_name,
                english: name.english_name,
            };
        })
        return data;
    },
    stateInit: (names) => {
        const data = {};
        names.forEach(name => {
            data[name.market] = {
                korean: name.korean_name,
                english: name.english_name,
            };
        })
        return data;
    }
};

const coinReadDataUtils = {
    updates: (names, state) => {
        const coinStateDatas = state.Coin.marketNames.data;
        console.log(coinStateDatas);
        names.forEach(name => {
            const newData = { ...coinStateDatas };
            let shortSym = name.code.split("-")[1];
            let moneySym = name.code.split("-")[0];

            if (shortSym === newData[shortSym]) {
                if (moneySym === "KRW") {
                    newData[shortSym] = {
                        ...newData[shortSym],
                        upbitPrice: name.code.trade_price
                    }
                    return newData;
                } else if (moneySym === "USDT") {
                    newData[shortSym] = {
                        ...newData[shortSym],
                        upbitUSDTPrice: name.code.trade_price
                    }
                    return newData;
                } else if (moneySym === "BTC") {
                    newData[shortSym] = {
                        ...newData[shortSym],
                        upbitBTCPrice: name.code.trade_price
                    }
                    return newData;
                }
            }
        });
    },
    initNames: (names) => {
        const data = {};
        names.forEach(name => {
            let shortSym = name.split("-")[1];
            let moneySym = name.split("-")[0];

            if (moneySym === "KRW") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitPrice: 0.0
                }
            } else if (moneySym === "USDT") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitUSDTPrice: 0.0
                }
            } else if (moneySym === "BTC") {
                data[shortSym] = {
                    ...data[shortSym],
                    upbitBTCPrice: 0.0
                }
            }
        });
        return data;
    }
};


export {
    coinListDataUtils,
    coinReadDataUtils
}
